import React, { useRef, useEffect, useState } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import jsPDF from "jspdf";

const socket = io("http://localhost:5000");

const Whiteboard = () => {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const query = new URLSearchParams(location.search);

  const mode = query.get("mode");
  const roomType = query.get("type") || "public";
  const password = query.get("password") || "";
  const isEditable = mode !== "view";
  const username = location.state?.username || "";

  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [tool, setTool] = useState("pen");
  const [color, setColor] = useState("#000000");
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [participants, setParticipants] = useState([]);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  useEffect(() => {
    if (!username) {
      alert("Username missing. Redirecting...");
      navigate("/");
      return;
    }

    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const ctx = canvas.getContext("2d");
    ctx.lineCap = "round";
    ctx.lineWidth = 3;
    ctx.strokeStyle = color;
    ctx.font = "20px Arial";
    ctxRef.current = ctx;

    socket.emit("join-room", {
      roomId,
      username,
      roomType,
      password,
    });

    socket.on("error-message", (msg) => {
      alert(msg);
      navigate("/");
    });

    socket.on("participants-updated", (users) => {
      setParticipants(users);
    });

    socket.on("draw", ({ tool, startX, startY, x, y, color, text }) => {
      const ctx = ctxRef.current;
      ctx.strokeStyle = tool === "eraser" ? "#ffffff" : color;
      ctx.fillStyle = ctx.strokeStyle;
      ctx.lineWidth = tool === "eraser" ? 20 : 3;

      if (tool === "pen" || tool === "eraser") {
        ctx.lineTo(x, y);
        ctx.stroke();
      } else if (tool === "rect") {
        ctx.strokeRect(startX, startY, x - startX, y - startY);
      } else if (tool === "circle") {
        const radius = Math.sqrt(Math.pow(x - startX, 2) + Math.pow(y - startY, 2));
        ctx.beginPath();
        ctx.arc(startX, startY, radius, 0, Math.PI * 2);
        ctx.stroke();
      } else if (tool === "text") {
        ctx.fillText(text, x, y);
      }
    });

    socket.on("clear", () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    });

    return () => {
      socket.emit("leave-room", { roomId, username });
    };
  }, [roomId, username, password, roomType]);

  // Drawing logic remains unchanged...

  const clearCanvas = () => {
    if (!isEditable) return;
    const canvas = canvasRef.current;
    ctxRef.current.clearRect(0, 0, canvas.width, canvas.height);
    socket.emit("clear", roomId);
  };

  const downloadAsImage = () => {
    const canvas = canvasRef.current;
    const link = document.createElement("a");
    link.download = "whiteboard.png";
    link.href = canvas.toDataURL();
    link.click();
  };

  const downloadAsPDF = () => {
    const canvas = canvasRef.current;
    const imgData = canvas.toDataURL("image/png");
    const pdf = new jsPDF("landscape", "px", [canvas.width, canvas.height]);
    pdf.addImage(imgData, "PNG", 0, 0, canvas.width, canvas.height);
    pdf.save("whiteboard.pdf");
  };

  return (
    <div>
      <div style={{ position: "fixed", top: 10, left: 10, background: "#fff", padding: 10, borderRadius: 8 }}>
        <div><strong>Room:</strong> {roomId}</div>
        <div><strong>Mode:</strong> {mode}</div>
        <div><strong>Type:</strong> {roomType}</div>
        <div><strong>You:</strong> {username}</div>

        <select onChange={(e) => setTool(e.target.value)} value={tool} disabled={!isEditable}>
          <option value="pen">Pen</option>
          <option value="rect">Rectangle</option>
          <option value="circle">Circle</option>
          <option value="eraser">Eraser</option>
          <option value="text">Text</option>
        </select>

        <input type="color" value={color} onChange={(e) => setColor(e.target.value)} disabled={!isEditable} />
        <button onClick={clearCanvas} disabled={!isEditable}>Clear</button>
        <button onClick={downloadAsImage}>Export Image</button>
        <button onClick={downloadAsPDF}>Export PDF</button>
      </div>

      <div style={{ position: "fixed", top: 10, right: 10, background: "#f1f1f1", padding: 10, borderRadius: 8 }}>
        <strong>Participants:</strong>
        <ul style={{ paddingLeft: 16 }}>
          {participants.map((p, i) => <li key={i}>{p}</li>)}
        </ul>
      </div>

      <canvas
        ref={canvasRef}
        style={{ border: "1px solid #000", display: "block", background: "#fff" }}
        onMouseDown={isEditable ? startDrawing : null}
        onMouseMove={isEditable ? draw : null}
        onMouseUp={isEditable ? stopDrawing : null}
        onMouseLeave={() => setDrawing(false)}
      />
    </div>
  );
};

export default Whiteboard;
