import React, { useRef, useEffect, useState } from "react";
import { useParams, useLocation } from "react-router-dom";
import { io } from "socket.io-client";
import jsPDF from "jspdf";

const socket = io("http://localhost:5000");

const Whiteboard = () => {
  const { roomId } = useParams();
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const mode = query.get("mode");
  const isEditable = mode !== "view";

  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [tool, setTool] = useState("pen");
  const [color, setColor] = useState("#000000");
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const ctx = canvas.getContext("2d");
    ctx.lineCap = "round";
    ctx.lineWidth = 3;
    ctx.strokeStyle = color;
    ctx.font = "20px Arial";
    ctxRef.current = ctx;

    socket.emit("join-room", roomId);

    socket.on("draw", ({ tool, startX, startY, x, y, color, text }) => {
      const ctx = ctxRef.current;
      ctx.strokeStyle = tool === "eraser" ? "#ffffff" : color;
      ctx.fillStyle = ctx.strokeStyle;
      ctx.lineWidth = tool === "eraser" ? 20 : 3;

      if (tool === "pen" || tool === "eraser") {
        ctx.lineTo(x, y);
        ctx.stroke();
      } else if (tool === "rect") {
        ctx.strokeRect(startX, startY, x - startX, y - startY);
      } else if (tool === "circle") {
        const radius = Math.sqrt(Math.pow(x - startX, 2) + Math.pow(y - startY, 2));
        ctx.beginPath();
        ctx.arc(startX, startY, radius, 0, Math.PI * 2);
        ctx.stroke();
      } else if (tool === "text") {
        ctx.fillText(text, x, y);
      } else if (tool === "heart") {
        drawHeart(ctx, startX, startY, x, y, color);
      }
    });

    socket.on("clear", () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    });
  }, [roomId]);

  const startDrawing = ({ nativeEvent }) => {
    if (!isEditable) return;

    const { offsetX, offsetY } = nativeEvent;
    const ctx = ctxRef.current;

    if (tool === "text") {
      const userText = prompt("Enter text:");
      if (userText) {
        ctx.fillStyle = color;
        ctx.fillText(userText, offsetX, offsetY);
        socket.emit("draw", { roomId, tool: "text", x: offsetX, y: offsetY, color, text: userText });
      }
      return;
    }

    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setStartPos({ x: offsetX, y: offsetY });
    setDrawing(true);

    const snapshot = ctx.getImageData(0, 0, canvasRef.current.width, canvasRef.current.height);
    setHistory((prev) => [...prev, snapshot]);
    setRedoStack([]);

    if (tool === "pen" || tool === "eraser") {
      socket.emit("draw", { roomId, tool, x: offsetX, y: offsetY, color });
    }
  };

  const draw = ({ nativeEvent }) => {
    if (!drawing || !isEditable || tool === "text") return;
    const { offsetX, offsetY } = nativeEvent;
    const ctx = ctxRef.current;

    ctx.putImageData(history[history.length - 1], 0, 0);
    ctx.strokeStyle = tool === "eraser" ? "#ffffff" : color;
    ctx.lineWidth = tool === "eraser" ? 20 : 3;

    if (tool === "pen" || tool === "eraser") {
      ctx.lineTo(offsetX, offsetY);
      ctx.stroke();
      socket.emit("draw", { roomId, tool, x: offsetX, y: offsetY, color });
    } else if (tool === "rect") {
      ctx.strokeRect(startPos.x, startPos.y, offsetX - startPos.x, offsetY - startPos.y);
    } else if (tool === "circle") {
      const radius = Math.sqrt(Math.pow(offsetX - startPos.x, 2) + Math.pow(offsetY - startPos.y, 2));
      ctx.beginPath();
      ctx.arc(startPos.x, startPos.y, radius, 0, Math.PI * 2);
      ctx.stroke();
    } else if (tool === "heart") {
      drawHeart(ctx, startPos.x, startPos.y, offsetX, offsetY, color);
    }
  };

  const stopDrawing = ({ nativeEvent }) => {
    if (!isEditable) return;
    setDrawing(false);
    const { offsetX, offsetY } = nativeEvent;

    if (["rect", "circle", "heart"].includes(tool)) {
      socket.emit("draw", {
        roomId,
        tool,
        startX: startPos.x,
        startY: startPos.y,
        x: offsetX,
        y: offsetY,
        color,
      });
    }

    ctxRef.current.closePath();
  };

  const clearCanvas = () => {
    if (!isEditable) return;
    const canvas = canvasRef.current;
    ctxRef.current.clearRect(0, 0, canvas.width, canvas.height);
    socket.emit("clear", roomId);
  };

  const drawHeart = (ctx, x1, y1, x2, y2, color) => {
    const width = x2 - x1;
    const height = y2 - y1;
    const centerX = x1 + width / 2;
    const centerY = y1 + height / 2;
    const scale = Math.min(Math.abs(width), Math.abs(height)) / 15;

    ctx.beginPath();
    ctx.moveTo(centerX, centerY + scale * 2);
    for (let i = 0; i <= Math.PI * 2; i += 0.01) {
      const x = 16 * Math.sin(i) ** 3;
      const y = 13 * Math.cos(i) - 5 * Math.cos(2 * i) - 2 * Math.cos(3 * i) - Math.cos(4 * i);
      ctx.lineTo(centerX + x * scale, centerY - y * scale);
    }
    ctx.closePath();
    ctx.strokeStyle = color;
    ctx.stroke();
  };

  const downloadAsImage = () => {
    const canvas = canvasRef.current;
    const link = document.createElement("a");
    link.download = "whiteboard.png";
    link.href = canvas.toDataURL();
    link.click();
  };

  const downloadAsPDF = () => {
    const canvas = canvasRef.current;
    const imgData = canvas.toDataURL("image/png");
    const pdf = new jsPDF("landscape", "px", [canvas.width, canvas.height]);
    pdf.addImage(imgData, "PNG", 0, 0, canvas.width, canvas.height);
    pdf.save("whiteboard.pdf");
  };

  return (
    <>
      <style>{`
        .toolbar {
          position: fixed;
          top: 1rem;
          left: 1rem;
          background: #ffffffdd;
          border: 1px solid #ddd;
          padding: 1rem;
          border-radius: 12px;
          box-shadow: 0 2px 6px rgba(0,0,0,0.1);
          z-index: 10;
          font-family: sans-serif;
          width: 280px;
        }

        .toolbar label,
        .toolbar select,
        .toolbar button,
        .toolbar input[type="color"] {
          display: block;
          margin: 8px 0;
          width: 100%;
          font-size: 14px;
        }

        .toolbar select,
        .toolbar input[type="color"],
        .toolbar button {
          padding: 8px;
          border-radius: 8px;
          border: 1px solid #ccc;
          cursor: pointer;
        }

        .toolbar .color-swatch {
          display: flex;
          gap: 6px;
          margin-top: 8px;
        }

        .toolbar .color-swatch button {
          width: 24px;
          height: 24px;
          border-radius: 50%;
          border: 1px solid #ccc;
          cursor: pointer;
        }

        .toolbar .room-info {
          font-size: 12px;
          color: #444;
          margin-bottom: 12px;
        }
      `}</style>

      <div className="toolbar">
        <div className="room-info">
          <strong>Room:</strong> {roomId}<br />
          <strong>Mode:</strong> {mode === "view" ? "View-only" : "Editable"}
        </div>

        <label htmlFor="tool">Tool</label>
        <select id="tool" onChange={(e) => setTool(e.target.value)} value={tool} disabled={!isEditable}>
          <option value="pen">Pen</option>
          <option value="rect">Rectangle</option>
          <option value="circle">Circle</option>
          <option value="heart">Heart</option>
          <option value="text">Text</option>
          <option value="eraser">Eraser</option>
        </select>

        <label htmlFor="color">Color</label>
        <input type="color" id="color" value={color} onChange={(e) => setColor(e.target.value)} disabled={!isEditable} />

        <div className="color-swatch">
          {["#000000", "#FF0000", "#00FF00", "#0000FF", "#FFFF00", "#FFFFFF"].map((c) => (
            <button
              key={c}
              style={{ backgroundColor: c }}
              onClick={() => setColor(c)}
              disabled={!isEditable}
            />
          ))}
        </div>

        <button onClick={clearCanvas} disabled={!isEditable}>Clear</button>
        <button onClick={downloadAsImage}>Export as Image</button>
        <button onClick={downloadAsPDF}>Export as PDF</button>
      </div>

      <canvas
        ref={canvasRef}
        style={{ border: "1px solid #000", display: "block", background: "#fff" }}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={() => setDrawing(false)}
      />
    </>
  );
};

export default Whiteboard;
