import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";

const Home = () => {
  const [roomId, setRoomId] = useState("");
  const [mode, setMode] = useState("edit");
  const navigate = useNavigate();

  const handleJoin = (e) => {
    e.preventDefault();
    const id = roomId.trim() === "" ? uuidv4().slice(0, 6) : roomId.trim();
    navigate(`/room/${id}?mode=${mode}`);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h2>ðŸŽ¨ Collaborative Whiteboard</h2>
      <form onSubmit={handleJoin}>
        <input
          type="text"
          placeholder="Enter room ID or leave blank"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          style={{ padding: "10px", marginRight: "10px" }}
        />
        <select value={mode} onChange={(e) => setMode(e.target.value)} style={{ padding: "10px" }}>
          <option value="edit">Edit</option>
          <option value="view">View-only</option>
        </select>
        <br /><br />
        <button type="submit" style={{ padding: "10px 20px" }}>Join Room</button>
      </form>
    </div>
  );
};

export default Home;
