import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import { v4 as uuidv4 } from 'uuid';

const socket = io('http://localhost:5000');

const Whiteboard = () => {
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const [color, setColor] = useState('#000000');
  const [tool, setTool] = useState('pen');
  const [drawing, setDrawing] = useState(false);
  const [participants, setParticipants] = useState([]);
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState('');
  const [myStrokes, setMyStrokes] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const startX = useRef(0);
  const startY = useRef(0);

  const { room } = useParams();

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth - 40;
    canvas.height = window.innerHeight - 150;
    const ctx = canvas.getContext('2d');
    ctx.lineCap = 'round';
    ctx.strokeStyle = color;
    ctx.lineWidth = 2;
    ctxRef.current = ctx;
  }, []);

  useEffect(() => {
    if (!room) {
      const newRoomId = uuidv4();
      setRoomId(newRoomId);
      socket.emit('create-room', { roomId: newRoomId, isPrivate, password });
    } else {
      setRoomId(room);
      socket.emit('join-room', { roomId: room, username, password });
    }

    socket.on('users-update', (users) => setParticipants(users));

    socket.on('draw', ({ x0, y0, x1, y1, color }) => {
      drawLine(x0, y0, x1, y1, color, false, false);
    });

    socket.on('undo', () => {
      setMyStrokes(prev => {
        const updated = [...prev];
        updated.pop();
        redrawFromStrokes(updated);
        return updated;
      });
    });

    socket.on('redo', (stroke) => {
      drawLine(stroke.x0, stroke.y0, stroke.x1, stroke.y1, stroke.color, false, false);
      setMyStrokes(prev => [...prev, stroke]);
    });

    socket.on('clear-canvas', () => {
      ctxRef.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      setMyStrokes([]);
      setRedoStack([]);
    });
  }, [room]);

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    startX.current = offsetX;
    startY.current = offsetY;
    setDrawing(true);
  };

  const finishDraw = ({ nativeEvent }) => {
    if (!drawing) return;
    setDrawing(false);
    const { offsetX, offsetY } = nativeEvent;
    handleDraw(startX.current, startY.current, offsetX, offsetY, true);
  };

  const draw = ({ nativeEvent }) => {
    if (!drawing || (tool !== 'pen' && tool !== 'eraser')) return;
    const { offsetX, offsetY } = nativeEvent;
    handleDraw(startX.current, startY.current, offsetX, offsetY, true);
    startX.current = offsetX;
    startY.current = offsetY;
  };

  const handleDraw = (x0, y0, x1, y1, emit = true) => {
    let col = tool === 'eraser' ? '#ffffff' : color;
    drawLine(x0, y0, x1, y1, col, emit);
  };

  const drawLine = (x0, y0, x1, y1, color, emit, save = true) => {
    const ctx = ctxRef.current;
    ctx.strokeStyle = color;
    ctx.beginPath();
    ctx.moveTo(x0, y0);
    ctx.lineTo(x1, y1);
    ctx.stroke();
    ctx.closePath();

    if (save) setMyStrokes(prev => [...prev, { x0, y0, x1, y1, color }]);

    if (emit) socket.emit('draw', { x0, y0, x1, y1, color });
  };

  const redrawFromStrokes = (strokes) => {
    const ctx = ctxRef.current;
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    strokes.forEach(s => drawLine(s.x0, s.y0, s.x1, s.y1, s.color, false, false));
  };

  const drawShape = (type) => {
    const ctx = ctxRef.current;
    const x = startX.current;
    const y = startY.current;
    const size = 100;
    ctx.strokeStyle = color;
    ctx.beginPath();
    if (type === 'rect') ctx.strokeRect(x, y, size, size);
    else if (type === 'circle') ctx.arc(x, y, size / 2, 0, Math.PI * 2);
    else if (type === 'heart') {
      ctx.moveTo(x, y);
      ctx.bezierCurveTo(x + 25, y - 25, x + 75, y + 25, x, y + 75);
      ctx.bezierCurveTo(x - 75, y + 25, x - 25, y - 25, x, y);
    }
    ctx.stroke();
    ctx.closePath();
  };

  const handleUndo = () => {
    if (myStrokes.length === 0) return;
    const updated = [...myStrokes];
    const last = updated.pop();
    setMyStrokes(updated);
    setRedoStack(prev => [...prev, last]);
    redrawFromStrokes(updated);
    socket.emit('undo');
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;
    const stroke = redoStack.pop();
    setRedoStack([...redoStack]);
    setMyStrokes(prev => [...prev, stroke]);
    drawLine(stroke.x0, stroke.y0, stroke.x1, stroke.y1, stroke.color, true, false);
    socket.emit('redo', stroke);
  };

  const clearCanvas = () => {
    ctxRef.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    setMyStrokes([]);
    setRedoStack([]);
    socket.emit('clear-canvas');
  };

  const exportImage = () => {
    const url = canvasRef.current.toDataURL('image/png');
    const link = document.createElement('a');
    link.download = 'whiteboard.png';
    link.href = url;
    link.click();
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>üßë‚Äçüé® Collaborative Whiteboard</h2>
      <div>
        <input placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
        <input placeholder="Room Password" onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div style={{ margin: '10px' }}>
        <button onClick={() => setTool('pen')}>Pen</button>
        <button onClick={() => setTool('eraser')}>Eraser</button>
        <button onClick={() => drawShape('rect')}>Rectangle</button>
        <button onClick={() => drawShape('circle')}>Circle</button>
        <button onClick={() => drawShape('heart')}>‚ù§Ô∏è Heart</button>
        <button onClick={() => setTool('text')}>Text</button>
        <input type="color" onChange={(e) => setColor(e.target.value)} />
        <button onClick={handleUndo}>‚Ü© Undo</button>
        <button onClick={handleRedo}>‚Ü™ Redo</button>
        <button onClick={clearCanvas}>üßπ Clear</button>
        <button onClick={exportImage}>üì§ Export</button>
      </div>
      <canvas
        ref={canvasRef}
        onMouseDown={startDraw}
        onMouseUp={finishDraw}
        onMouseMove={draw}
        style={{ border: '2px solid black', backgroundColor: 'white', cursor: 'crosshair' }}
      />
      <div>
        <h4>üë• Participants:</h4>
        <ul>
          {participants.map((user, index) => <li key={index}>{user}</li>)}
        </ul>
      </div>
    </div>
  );
};

export default Whiteboard;
