import React, { useRef, useEffect, useState } from "react";
import { io } from "socket.io-client";
import jsPDF from "jspdf";

const socket = io("http://localhost:5000"); // Replace with your backend URL if deployed

const App = () => {
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const roomId = "default-room"; // Replace with dynamic value later if needed

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const ctx = canvas.getContext("2d");
    ctx.lineCap = "round";
    ctx.strokeStyle = "black";
    ctx.lineWidth = 3;
    ctxRef.current = ctx;

    // Join room
    socket.emit("join-room", roomId);

    // Receive draw events
    socket.on("draw", ({ x, y, type }) => {
      if (type === "begin") {
        ctx.beginPath();
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
        ctx.stroke();
      }
    });

    // Receive clear event
    socket.on("clear", () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    });
  }, []);

  const startDrawing = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    ctxRef.current.beginPath();
    ctxRef.current.moveTo(offsetX, offsetY);
    setDrawing(true);
    socket.emit("draw", { roomId, x: offsetX, y: offsetY, type: "begin" });

    const imageData = ctxRef.current.getImageData(0, 0, canvasRef.current.width, canvasRef.current.height);
    setHistory((prev) => [...prev, imageData]);
    setRedoStack([]); // Clear redo stack
  };

  const draw = ({ nativeEvent }) => {
    if (!drawing) return;
    const { offsetX, offsetY } = nativeEvent;
    ctxRef.current.lineTo(offsetX, offsetY);
    ctxRef.current.stroke();
    socket.emit("draw", { roomId, x: offsetX, y: offsetY, type: "draw" });
  };

  const stopDrawing = () => {
    setDrawing(false);
    ctxRef.current.closePath();
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    ctxRef.current.clearRect(0, 0, canvas.width, canvas.height);
    socket.emit("clear", roomId);
  };

  const undo = () => {
    if (history.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    const newHistory = [...history];
    const last = newHistory.pop();
    setRedoStack((r) => [...r, ctx.getImageData(0, 0, canvas.width, canvas.height)]);
    ctx.putImageData(last, 0, 0);
    setHistory(newHistory);
  };

  const redo = () => {
    if (redoStack.length === 0) return;
    const canvas = canvasRef.current;
    const ctx = ctxRef.current;
    const newRedo = [...redoStack];
    const next = newRedo.pop();
    setHistory((h) => [...h, ctx.getImageData(0, 0, canvas.width, canvas.height)]);
    ctx.putImageData(next, 0, 0);
    setRedoStack(newRedo);
  };

  const downloadAsImage = () => {
    const canvas = canvasRef.current;
    const link = document.createElement("a");
    link.download = "whiteboard.png";
    link.href = canvas.toDataURL();
    link.click();
  };

  const downloadAsPDF = () => {
    const canvas = canvasRef.current;
    const imgData = canvas.toDataURL("image/png");
    const pdf = new jsPDF("landscape", "px", [canvas.width, canvas.height]);
    pdf.addImage(imgData, "PNG", 0, 0, canvas.width, canvas.height);
    pdf.save("whiteboard.pdf");
  };

  return (
    <div>
      <div style={{ position: "fixed", top: 10, left: 10, zIndex: 1 }}>
        <button onClick={undo}>Undo</button>
        <button onClick={redo}>Redo</button>
        <button onClick={clearCanvas}>Clear</button>
        <button onClick={downloadAsImage}>Export Image</button>
        <button onClick={downloadAsPDF}>Export PDF</button>
      </div>
      <canvas
        ref={canvasRef}
        style={{ border: "1px solid #000", display: "block" }}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
      />
    </div>
  );
};

export default App;
